import BasePage from './base.page';

class shoppingcartPage extends BasePage {
    get buttonShoppincart() {return $('#cart-total')}
    get total() {return $('//tr[4]/td[2]')}
    get buttonRemove() {return $('.btn.btn-danger.btn-xs')}
    get message() {return $('//li/p')} 

    /**
     * Ingresar al shopping cart
     */
    async ingresarAlCarrito() {
        await super.clickearElemento(this.buttonShoppincart);
    }

    /**
     * Obtener el precio total del carrito 
     */
    async totalPriceShoppingcart() {
        return await this.total.getText()
    }



    /**
     * Eliminar producto del carrito
     */
    async removeProduct() {
        await super.clickearElemento(this.buttonRemove)
    }

    /**
     * Obtener alerta de que el carrito esta vacio
     */
    async allertMessage() {
        return await this.message.getText()
    }


}
export default new shoppingcartPage();
